mapper.select:
SELECT b.cal, a.no
FROM sandwich a
LEFT OUTER JOIN nutrient b on a.code = b.code ORDER BY no;

SELECT b.cal, a.no
FROM wrap a
LEFT OUTER JOIN nutrient b on a.code = b.code ORDER BY no;

